import React, {Component} from 'react'
import logo from './logo.svg';
import './App.css';
import PatientList from './Components/PatientList';
import PatientMedications from './Components/PatientMedications';

class PatientView extends Component {

  constructor(props)
    {
        super(props);

        this.state = {
          items: [],
          SelectedPatientId : null
        }
    }

    fetchPatientDetail = (e) =>
    {
      fetch("http://localhost:3002/Patients/Medications/" + this.State.SelectedPatientId + ",{ method:'Get'}")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                       items : result,
                       isLoaded : true
                    });
                },
                (error) => {
                    this.setState({
                        error : error
                    });
                }
            )
    }

    PatientSelected = (e) => {
            this.setState({SelectedPatientId : e.target.value});
            fetchPatientDetail();
    }


  render()
  {
  return (
    <div className="App">
      <header className="App-header">
        <p>
          <PatientList PatientSelected={this.PatientSelected} ></PatientList>
          <PatientMedications SelectedPatientId={this.state.SelectedPatientId}> </PatientMedications>
        </p>
      </header>
    </div>
  );
}
}


export default PatientView;
