import React, {Component} from 'react'

class PatientList extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items : [],
            length: 0
        }
    }

    componentDidMount()
    {
        fetch("http://localhost:3002/Patients/",{ method:'Get',  mode: 'no-cors'})
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                       items : result,
                       isLoaded : true
                    });
                },
                (error) => {
                    this.setState({
                        error : error
                    });
                }
            )

