import React, {Component} from 'react'
import logo from './logo.svg';
import './App.css';
import PatientList from './Components/PatientList';
import PatientMedications from './Components/PatientMedications';

class PatientView extends Component {

  constructor(props)
    {
        super(props);

        this.state = {
          items: [],
          SelectedPatientId : null
        }

       this.saySomething = this.saySomething.bind(this);
    }

    saySomething(something) {
      console.log(something);
  }

    PatientSelected = (e) => {
          //this.setState({SelectedPatientId : e.target.value});

          var all = "http://localhost:3002/Patients/"+e.target.value ;
          alert(all)

          fetch("http://localhost:3002/Patients/"+e.target.value,{ method:'Get'})
            .then(res => res.json())
            .then(
                (result) => {
                  alert(result)
                    this.setState({
                       items : result,
                       isLoaded : true
                    });
                },
                (error) => {
                    this.setState({
                        error : error
                    });
                }
        )


    }


  render()
  {
    console.log(this.state.items)
   return (



    <div className="App">
      <header className="App-header">
        <p>
          <PatientList PatientSelected={this.PatientSelected} ></PatientList>
          <PatientMedications SelectedPatientId={this.state.SelectedPatientId}> </PatientMedications>
          Temperature: <input value={this.state.items.temperature}></input> <br/>
          Pulse: <input value={this.state.items.pulse}></input>

          <div >
            <select style={{width:"150px"}} size="10" onChange = {this.props.PatientSelected} >
                    {this.state.items.map(item => (
                        <option value = {item._id}>
                                {item.firstName} {item.lastName}
                        </option>
                    ))}
                </select>
            </div>
        </p>
      </header>
    </div>
  );
}
}


export default PatientView;
