import React, {Component} from 'react'
import logo from './logo.svg';
import './App.css';
import PatientList from './Components/PatientList';
import PatientMedications from './Components/PatientMedications';

class PatientView extends Component {

  constructor(props)
    {
        super(props);

        this.state = {
          items: [],
          SelectedPatientId
        }

        this.SelectedPatientId = "";
    }

    componentWillReceiveProps(nextProps)
    {
        if ( nextProps.Employee_Id !== this.props.Employee_Id  )
        {
            console.log('id got updated...'+ nextProps.Employee_Id)
        }
    }

    fetchMedications = () =>
    {
      fetch("http://localhost:3002/Patients/Medications/" + this.SelectedPatientId + ",{ method:'Get'}")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                       items : result,
                       isLoaded : true
                    });
                },
                (error) => {
                    this.setState({
                        error : error
                    });
                }
            )
    }

    PatientSelected = (e) => {
            console.log(e.target.value)
            this.SelectedPatientId = e.target.value;
            this.setState({SelectedPatientId : e.target.value});
           // fetchMedications ();
          //  alert(this.state.items);
    }


  render()
  {
  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        */}
        <p>
          <PatientList PatientSelected={this.PatientSelected} ></PatientList>
          <PatientMedications SelectedPatientId={this.state.SelectedPatientId}> </PatientMedications>
        </p>
      </header>
    </div>
  );
}
}


export default PatientView;
