import React, {Component} from 'react'
import PropTypes from 'prop-types';

class PatientMedications extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {
            error: null,
            items : []
        }
    }

    componentWillReceiveProps(nextProps)
    {
        if ( nextProps.SelectedPatientId !== this.props.SelectedPatientId  )
        {
           //alert("http://localhost:3002/Patients/Medications/" + nextProps.SelectedPatientId + ",{ method:'Get'}");

           fetch("http://localhost:3002/Patients/Medications/5d4c72e8cc74762e9933e6d8",{ method:'Get'})
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                       items : result
                    });
                },
                (error) => {
                    this.setState({
                        error : error
                    });
                }
            )
        }
    }


    render()
    {
        return (

            <div >
            <   select style={{width:"150px"}} size="10">
                    {this.state.items.map(item => (
                        <option key = {item.medname} value = {item.medname}>
                                {item.medname} 
                        </option>
                    ))}
                </select>
            </div>
        );
    }

}
PatientMedications.popTypes = {
    SelectedPatientId: PropTypes.string.isRequired
}

export default PatientMedications;
