import React, {Component} from 'react'

class PatientList extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items : []
        }
    }

    componentDidMount()
    {
        fetch("http://localhost:3002/Patients/",{ method:'Get',  mode: 'no-cors'})
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                       items : result,
                       isLoaded : true
                    });
                },
                (error) => {
                    this.setState({
                        error : error
                    });
                }
            )
    }

    render()
    {
        console.log("----------");
        console.log(this.state.isLoaded);
        return (

            <div >
            <select 
                    style={{width:"150px"}}
                    size="10" 
                     >

                {this.state.items.map(item => (
                    <option key={item._id} value={item._Id}>
                        {item.firstName} 
                    </option>
                ))}
                </select>
            </div>
        );
    }

}

export default PatientList;
